mapper.update:
UPDATE t_user
SET auth = null
WHERE uid = #{uid} AND auth = #{auth}

mapper.select:
SELECT *
FROM t_user
WHERE uid = #{uid} AND auth IS NULL AND provider = #{provider}

mapper.update:
